package main;

public class Ex01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x;					//정수형 변수 x를 선언
		x = 10;					//x 변수에 10을 대입
		System.out.println(x);  //x를 출력하면 변수에 담긴 값 출력하면
		
		System.out.println("a");
		System.out.print("b");
		System.out.print("c");
		
		//sysout -> 컨트롤 + 스페이스 -> 코드 자동완성 (system.out.println)
		
		System.out.println();
		

	}

}
